import React from "react";
import { useState, useEffect } from "react";
// import Home from "./Home";

export default function WelcomeScreen({ children }) {
  const [password, setPassword] = useState("");
  const [err, setErr] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const expectedPassword = "iamadinkra";
  const loginKey = "isLoggedIn";
  useEffect(() => {
    const storedLogin = localStorage.getItem(loginKey);
    if (storedLogin) {
      setIsLoggedIn(true);
    }
  }, []);

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (password === expectedPassword) {
      setIsLoggedIn(true);
      setPassword("");
      setErr(null);
      localStorage.setItem(loginKey, true);
    } else {
      setErr("Invalid Credentials");
    }
  };

  if (isLoggedIn) {
    setTimeout(() => {
      localStorage.clear();
    }, 10000);
    return (
      // form validation
      <div>{children}</div>
    );
  } else {
    return (
      // login form

      <>
        <main className="">
          <form onSubmit={handleSubmit} className="">
            <div>
              <h1 className="">Natasha & Manuel</h1>
              {/* <p className="holi text-1xl font-light">Welcome</p> */}
            </div>
            <div className="">
              <input
                className=""
                type="password"
                value={password}
                onChange={handlePasswordChange}
                placeholder="password"
                required
              />
              <button type="submit" className="">
                LogIn
              </button>
              {err && <div className="">{err}</div>}
            </div>
          </form>
        </main>
        <div className="">
          <p>©️Copyright 2023. KAIZEN</p>
        </div>
      </>
    );
  }
}
