import { useState, useEffect } from "react";
import WelcomeScreen from "./WelcomeScreen";
import "./App.css";
import "./index.css";

function App() {
  const [password, setPassword] = useState("");
  const [err, setErr] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const expectedPassword = "iamadinkra";
  const loginKey = "isLoggedIn";
  useEffect(() => {
    const storedLogin = localStorage.getItem(loginKey);
    if (storedLogin) {
      setIsLoggedIn(true);
    }
  }, []);

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (password === expectedPassword) {
      setIsLoggedIn(true);
      setPassword("");
      setErr(null);
      localStorage.setItem(loginKey, true);
    } else {
      setErr("Invalid Credentials");
    }
  };

  if (isLoggedIn) {
    setTimeout(() => {
      localStorage.clear();
    }, 10000);
    return (
      // landing page
      <>
        <WelcomeScreen />
      </>
    );
  } else {
    return (
      // form validation
      <>
        <main className="bg-white h-screen flex items-center justify-center">
          <form
            onSubmit={handleSubmit}
            className=" flex flex-col items-center text-center w-1/2 space-y-12 py-28"
          >
            <div className="space-y-12">
              <h1 className="font-light text-6xl ">iamadinkra</h1>
              <p className="holi text-8xl font-light">Welcome</p>
            </div>
            <div className="flex flex-col gap-y-10">
              <input
                className="border-2 px-4 py-2 rounded-md"
                type="password"
                value={password}
                onChange={handlePasswordChange}
                placeholder="password"
                required
              />
              <button
                type="submit"
                className="bg-black py-2 text-white rounded-md hover:bg-gray-800 transition-all"
              >
                Submit
              </button>
              {err && <div className="text-red-500">{err}</div>}
            </div>
          </form>
        </main>
      </>
    );
  }
}

export default App;
