import React from "react";

export const Header = () => {
  return (
    <header className=" h-max">
      <div className="intro-img relative bg-headerBg w-11/12 h-screen flex items-center justify-center p-5 bg-cover bg-center">
        <div className="absolute top-40 translate-x-20 right-0 bg-theme1 text-theme3 py-2 px-8 text-center">
          <h2 className="cust-font text-5xl font-bold">Save the date</h2>
          <p className="cust-font text-3xl font-bold">00/00/00</p>
        </div>
        <h1 className="cust-font text-9xl text-theme1">A day of joy</h1>
      </div>
    </header>
  );
};
